            },
            body: "url=" + config.url + "&time=" + config.time + "&key=" + config.key,
            method: "POST"
        });
        var json = JSON.parse(apiHtml);
        if (json.code == 200) {
            playUrl = json.url;
            return toUrl(playUrl);
        } else {
            return 'toast://' + json.msg;
        }

