var d = [];
var html = getResCode();

var lazy = $('').lazyRule(() => {
    function webSniffer(input) {//嗅探
        showLoading('尝试获取视频中...');
        var video =
            'webRule://' +
            input +
            '@' +
            $.toString(() => {
                var urls = _getUrls();
                var exclude = /\/404\.m3u8|\/xiajia\.mp4|\/余额不足\.m3u8|\.css|\.js|\.gif|\.png|\.jpg|\.jpeg|html,http|m3u88.com\/admin|\.php\?v=h|\?url=h|\&url=h|\?vid=h|%253Furl%253Dh/; //设置排除地址
                var contain = /\.mp4|\.m3u8|\.flv|\.avi|\.mpeg|\.wmv|\.mov|\.rmvb/; //设置符合条件的正确地址
                for (var k in urls) {
                    if (!exclude.test(urls[k]) && contain.test(urls[k])) {
                        if (/mgtv|sohu/.test(urls[k])) {
                            return urls[k] + ';{User-Agent@Mozilla/5.0 (Windows NT 10.0)}';
                        } 
                        else if 
                     (/lz|ffzy/.test(urls[k]))
                    {
                        return fy_bridge_app.clearM3u8Ad(urls[k]);
                    }
